import React, { useState } from "react";

function Tabuada() {
  const [numero, setNumero] = useState(""); 
  const [resultado, setResultado] = useState([]);  


  const gerarTabuada = (num) => {
    let tabuada = [];
    for (let i = 1; i <= 10; i++) {
      tabuada.push(`${num} x ${i} = ${num * i}`);
    }
    setResultado(tabuada);
  };
  const handleChange = (e) => {
    const valor = e.target.value;
    setNumero(valor);
    if (valor !== "") {
      gerarTabuada(valor);
    } else {
      setResultado([]);
    }
  };

  return (
    <div>
      <h1>Gerador de Tabuada</h1>
      <label htmlFor="numero">Digite o número da tabuada que você quer gerar: </label>
      <input
        id="numero"
        type="number"
        value={numero}
        onChange={handleChange}
      />
        <button onClick={gerarTabuada}

    />

      {}
      <ul>
        {resultado.map((linha, index) => (
          <li key={index}>{linha}</li>
        ))}
      </ul>
    </div>
  );
}

export default Tabuada;
